SELECT
    zsa.id,
    zsi.id staffId,
    zsi.ryname,
    zsi.sex,
    zsi.csdat,
    zsi.idnumber,
    zsi.jkzt,
    zsi.zhrjg,
    zsi.zhrsg,
    zsi.zhrtz,
    zsi.zhrxx,
    zsi.zhrmz,
    zsi.isfcdj,
    zsi.zzmm,
    zsi.hyzt,
    zsi.zngs,
    zsi.zyks1,
    zsi.zyks2,
    zsi.zgxl,
    zsi.wysp,
    zsi.wyspfs,
    zsi.jsjsp,
    zsi.tel,
    zsi.jtzz,
    zsi.xjdz,
    zsi.hjdz,
    zsi.jjlxr,
    zsi.lxrtel,
    zsi.email,
    zsi.indw,
    zsi.wechatNo,
    zsi.isyszgz,
    zsi.zylx,
    zsi.isbysqdzyys,
    zsi.isssjdszhy,
    zsi.ywjwbs,
    zse.yxname,
    zsa.posterId,
    zsa.state applystate,
    zsa.CREATE_TIME,
    zsa.crtdat
FROM
    zp_staff_apply zsa
    INNER JOIN zp_staff_info zsi on
        zsa.staffId = zsi.id
    LEFT JOIN zp_staff_education zse on
        zsi.id = zse.staffId
        and zse.xlxw = zsi.zgxl
WHERE
    zsa.posterId = #{zpPosterStaffView.posterId}
    and zsi.issub = 1

    and zsa.state = #{zpPosterStaffView.applystate}


        and zsi.ryname like concat('%', #{zpPosterStaffView.currencyField} ,'%')



